          price: "1500000,00ks",
          image:
            "data:image/jpeg;base64,",
        },
        {
          name: "iphone 12 pro",
          price: "1200000,00ks",
          image:
            "data:image/jpeg;base64,",
        },
      ],
    };
  },
  methods:{
    addItemToCart(product){    
      this.cart.push(product)
    }
  }
  
};
</script>

